const SearchFacets = ({ facets }: SearchFacetsProps) => {
  const ITEM_TO_LOAD = 5;

  const { onFacetClick } = useSearchResultsActions();

  const [facetsPagination, setFacetsPagination] = useState<FacepPagination[] | undefined>(
    undefined
  );

  useEffect(() => {
    const facetsPagination: FacepPagination[] = [];
    facets.map((f) => {
      const obj = {
        name: f.name,
        value: f.value,
        label: f.label,
        itemToLoad: ITEM_TO_LOAD,
      };
      facetsPagination.push(obj);
    });
    setFacetsPagination(facetsPagination);
  }, [facets]);

  const onShowMore = (index: number) => {
    setFacetsPagination((prevFacetsPagination) => {
      if (!prevFacetsPagination) return [];

      const newFacetsPagination = [...prevFacetsPagination];
      const item = { ...newFacetsPagination[index] };
      item.itemToLoad = ITEM_TO_LOAD + item.itemToLoad;
      newFacetsPagination[index] = item;

      return newFacetsPagination;
    });
  };

  const onFacetSearch = (e: ChangeEvent<HTMLInputElement>, index: number) => {
    const facetSearchText = e.target.value;
    if (facetSearchText) {
      setFacetsPagination((prevFacetsPagination) => {
        if (!prevFacetsPagination) return [];

        const newFacetsPagination = [...prevFacetsPagination];
        const item = { ...newFacetsPagination[index] };
        const filteredItems = item.value.filter((x) => {
          return x.text.toLocaleLowerCase().match(facetSearchText.toLocaleLowerCase());
        });
        item.value = filteredItems;
        newFacetsPagination[index] = item;

        return newFacetsPagination;
      });
    } else {
      const facetsPagination: FacepPagination[] = [];
      facets.map((f) => {
        const obj = {
          name: f.name,
          value: f.value,
          label: f.label,
          itemToLoad: ITEM_TO_LOAD,
        };
        facetsPagination.push(obj);
      });
      setFacetsPagination(facetsPagination);
    }
  };

  return (
    <SearchResultsAccordionFacets
      id="facets"
      defaultFacetTypesExpandedList={[]}
      onFacetValueClick={onFacetClick}
    >
      {facetsPagination &&
        facetsPagination.map((f, fIndex: number) => (
          <AccordionFacets.Facet
            facetId={f.name}
            key={fIndex}
            id={f.name + fIndex}
            className="mb-2 border-gray-200 w-full border gap-2 rounded px-4 py-4"
          >
            <div className="facets-header">
              <AccordionFacets.Header className="flex">
                <AccordionFacets.Trigger className="flex items-center justify-between w-full font-normal text-base">
                  {f.label}
                  <SvgIcon
                    className="facets-accordian-arrow"
                    viewBox="0 0 16 9"
                    size="xs"
                    icon="chevron-up"
                    fill="none"
                  />
                </AccordionFacets.Trigger>
              </AccordionFacets.Header>
            </div>
            <AccordionFacets.Content>
              <AccordionFacets.ValueList className="list-none mt-2 flex flex-col justify-start space-y-4">
                <div className="search-textbox relative w-full">
                  <input
                    type="text"
                    onChange={(e) => onFacetSearch(e, fIndex)}
                    placeholder="Search"
                    className="w-full rounded border px-2 h-[48px] border-black"
                  />
                  <div className="magnifier-icon absolute top-1/2 right-2 transform -translate-y-1/2">
                    <SvgIcon icon="magnifier" size="xs" viewBox="0 0 18 18" fill="none" />
                  </div>
                </div>
                {f.value.slice(0, f.itemToLoad).map((v, index: number) => (
                  <FacetItem
                    {...{
                      index,
                      facetValueId: v.id,
                    }}
                    key={index}
                    className="group flex text-sm cursor-pointer w-full"
                  >
                    <div className="facetlabel justify-between w-11/12">
                      <AccordionFacets.ItemCheckbox className="form-checkbox flex-none w-5 h-5 border border-gray-300 rounded cursor-pointer transition duration-500 ease-in-out hover:border-heading focus:outline-indigo-500 aria-checked:bg-indigo-500 aria-checked:hover:bg-heading aria-checked:focus:bg-heading">
                        <AccordionFacets.ItemCheckboxIndicator className="text-white w-5 h-5 ">
                          <CheckIcon />
                        </AccordionFacets.ItemCheckboxIndicator>
                      </AccordionFacets.ItemCheckbox>
                      <AccordionFacets.ItemLabel className="font-normal text-base font-modern ms-1">
                        {v.text}
                      </AccordionFacets.ItemLabel>
                    </div>
                    <div className="facetcount font-normal text-base text-dark-gray">
                      {v.count && `(${v.count})`}
                    </div>
                  </FacetItem>
                ))}
                {!(f.value.length > f.itemToLoad) ||
                  (f.value.length > ITEM_TO_LOAD && (
                    <div
                      onClick={() => {
                        onShowMore(fIndex);
                      }}
                      className="cursor-pointer show-more-facets flex gap-1 items-center w-11/12"
                    >
                      <SvgIcon icon="plus" fill="none" viewBox="0 0 12 11" size="xs" />
                      <h2 className="underline">Show more</h2>
                    </div>
                  ))}
              </AccordionFacets.ValueList>
            </AccordionFacets.Content>
          </AccordionFacets.Facet>
        ))}
    </SearchResultsAccordionFacets>
  );
};

const SearchPagination = ({ currentPage, totalPages }: SearchPaginationProps) => {
  const { onPageNumberChange } = useSearchResultsActions();
  return (
    <Pagination.Root
      currentPage={currentPage}
      defaultCurrentPage={1}
      totalPages={totalPages}
      onPageChange={(v) =>
        onPageNumberChange({
          page: v,
        })
      }
      className="flex gap-4"
    >
      <Pagination.PrevPage
        onClick={(e) => e.preventDefault()}
        className="cursor-pointer my-0 mx-2 data-[current=true]:hidden hover:text-indigo-500 focus:outline-indigo-500"
      >
        <SvgIcon icon="arrow-left" size="xs" viewBox="0 0 9 16" fill="none" />
      </Pagination.PrevPage>
      <Pagination.Pages>
        {(pagination) =>
          Pagination.paginationLayout(pagination, {
            boundaryCount: 1,
            siblingCount: 1,
          }).map(({ page, type }) =>
            type === 'page' ? (
              <Pagination.Page
                key={page}
                aria-label={`Page ${page}`}
                page={page as number}
                onClick={(e) => e.preventDefault()}
                className="py-xxs px-xs rounded-2xl cursor-pointer mx-2 data-[current=true]:text-white data-[current=true]:bg-dark-gray data-[current=true]:pointer-events-none data-[current=true]:no-underline"
              >
                {page}
              </Pagination.Page>
            ) : (
              <span key={type}>...</span>
            )
          )
        }
      </Pagination.Pages>
      <Pagination.NextPage
        onClick={(e) => e.preventDefault()}
        className="cursor-pointer my-0 mx-2 data-[current=true]:hidden hover:text-indigo-500 focus:outline-indigo-500"
      >
        <SvgIcon icon="arrow-right" size="xs" viewBox="0 0 9 16" fill="none" />
      </Pagination.NextPage>
    </Pagination.Root>
  );
};

const SortOrder = ({ options, selected }: SortOrderProps) => {
  const selectedSortIndex = options.findIndex((s) => s.name === selected);
  const { onSortChange } = useSearchResultsActions();
  return (
    <SortSelect.Root defaultValue={options[selectedSortIndex]?.name} onValueChange={onSortChange}>
      <SortSelect.Trigger className="cursor-pointer inline-flex items-center bg-transparent h-10 gap-1 border-0 focus:outline-indigo-500 justify-between w-full">
        <SortSelect.SelectValue>
          {selectedSortIndex > -1 ? options[selectedSortIndex].label : ''}
        </SortSelect.SelectValue>
        {/* <SortSelect.Icon /> */}
        <SvgIcon icon="sorting" viewBox="0 0 24 24" size="xs" />
      </SortSelect.Trigger>
      <SortSelect.Content className="bg-white shadow-[4px_4px_8px_#CFCFCF] z-[100] absolute top-6 focus-within:border-indigo-500 w-full ">
        <SortSelect.Viewport className="p-1 z-[50000]">
          {options.map((option: SearchResponseSortChoice) => (
            <SortSelect.Option
              value={option}
              key={option.name}
              className="flex items-center leading-none cursor-pointer select-none whitespace-no-wrap h-10 px-1 hover:bg-indigo-500 hover:text-white data-[state=checked]:text-indigo-500 data-[state=checked]:bg-white focus:outline-indigo-500"
            >
              <SortSelect.OptionText>{option.label}</SortSelect.OptionText>
            </SortSelect.Option>
          ))}
        </SortSelect.Viewport>
      </SortSelect.Content>
    </SortSelect.Root>
  );
};

